package com.market.pos.controller;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.jdbc.core.JdbcTemplate;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.List;import java.util.Map;@Controllerpublic class Hello {    @GetMapping("/hello")    private String hello(){        return "index";    }    @Autowired        private JdbcTemplate jdbcTemplate;    @RequestMapping("/sql")    public List<Map<String,Object>> getUser(){        String sql = "select * from pay_data_table";        List<Map<String, Object>> list = jdbcTemplate.queryForList(sql);        return list;    }}