package com.market.pos.mapper;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import com.market.pos.pojo.Users;import org.apache.ibatis.annotations.Mapper;import java.util.List;@Mapperpublic interface IUserMapper extends BaseMapper<Users> {    /**     * 通过userid查询用户     * @param userid     * @return     */    public List<Users> getUserByUserid(int userid);    /**     * 查询所有用户     * @return     */    public List<Users> getAllUser();    /**     * 新增用户     * @param users     */    public void insertUser(Users users);    /**     * 更新用户数据     * @param users     */    public void updateUser(Users users);    /**     * 通过userid删除用户     * @param userid     */    public void delUser(int userid);}