package com.market.pos.mapper;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import com.market.pos.pojo.Members;import org.apache.ibatis.annotations.Mapper;import java.util.List;@Mapperpublic interface MembersMapper extends BaseMapper<Members> {    /**     * 通过userid查询用户     * @param userid     * @return     */    public List<Members> getUserByUserid(String userid);    /**     * 查询所有用户     * @return     */    public List<Members> getAllUser();    /**     * 新增用户     * @param members     */    public void addMember(Members members);    /**     * 更新用户数据     * @param members     */    public void updateMember(Members members);    /**     * 通过userid删除用户     * @param userid     */    public void delMember(int userid);    public String findUser(String userid);    public String findPassword(String userid);}