package com.market.pos.tool.Equip;import com.market.pos.pojo.AskQQMessage;import com.market.pos.tool.connect.JdbcBackpack;import com.market.pos.tool.findTreasure.BackpackService;public class EquipIn {    public static String ask;    public static void BookIn(String qqid,String groupid,String equipname){        BackpackService.searchBackPack(qqid);        String equip_data = BackpackService.book;        String data_type = "book";        EquipUsersChar.usersChar(qqid,equipname,equip_data,groupid,data_type);        ask = EquipUsersChar.ask_book;    }    public static void equipmentIn(String qqid,String groupid,String equipname){        BackpackService.searchBackPack(qqid);        String equip_data = BackpackService.equipment;        String data_type = "equipment";        EquipUsersChar.usersChar(qqid,equipname,equip_data,groupid,data_type);        ask = EquipUsersChar.ask_equipment;    }}