package com.market.pos.tool.Equip;import com.market.pos.tool.connect.JdbcBackpack;public class EquipService {    public static String resultUserId;    public static String equipment;    public static String book;    public static String special;    public static int finddaily;    /**     * 通过只插入userid的形式新增用户，后续数据若要插入采用update方法     * @param qqid     */    public static void equipInsert(String qqid,String groupid){        String sql = "INSERT INTO equip ( userid ) VALUES ("                + "'"+ qqid+ "'" +                ")";        JdbcBackpack.insertBackPack(sql,groupid);    }    /**     * 通过userid更新其他字段，字段列名为data_type     * @param qqid     * @param data_type     * @param data     */    public static void equipUpdate(String qqid,String data_type,String data,String groupid){        String sql = "update equip set " + data_type + "=" + "'" +data+ "'" +"where userid = " + "'" +qqid+"'";        JdbcBackpack.updateBackPack(sql,groupid);    }    /**     * 通过qqid查询     * @param qqid     */    public static void equipSelect(String qqid,String groupid){        String sql = "select * from equip where userid = " +"'"+ qqid +"'";        JdbcBackpack.searchBackPack(sql,groupid);        resultUserId = JdbcBackpack.resultUserId;        equipment = JdbcBackpack.equipment;        special = JdbcBackpack.special;        book = JdbcBackpack.book;        finddaily = JdbcBackpack.finddaily;    }    public static void backpackSelect(String qqid,String groupid){        String sql = "select * from backpack where userid = " +"'"+ qqid +"'";        JdbcBackpack.searchBackPack(sql,groupid);        resultUserId = JdbcBackpack.resultUserId;        equipment = JdbcBackpack.equipment;        special = JdbcBackpack.special;        book = JdbcBackpack.book;        finddaily = JdbcBackpack.finddaily;    }}