package com.market.pos.tool.Equip;import com.google.gson.Gson;import com.market.pos.pojo.AskQQMessage;import com.market.pos.tool.findTreasure.BackpackService;public class OpenEquip {    public static String ask;    public static void openEquip(String qqid,String groupid){        AskQQMessage askQQMessage = new AskQQMessage();        askQQMessage.setAct("101");        askQQMessage.setQQID(qqid);        askQQMessage.setGroupid(groupid);        EquipService.equipSelect(qqid);        String equipment = EquipService.equipment;        String book = EquipService.book;        String special = EquipService.special;        if (equipment == null){            equipment = "无";        }        if (book == null){            book = "无";        }        if (special == null){            special = "无";        }        askQQMessage.setMsg("[CQ:at,qq=" +qqid+ "] 装备栏：" +                "\n[CQ:face,id=69]装备：" + equipment +                "\n[CQ:emoji,id=128591]秘籍：" + book +                "\n[CQ:emoji,id=10024]特殊物品：" + special);        ask = new Gson().toJson(askQQMessage);    }}