package com.market.pos.tool.common;import com.google.gson.Gson;import com.market.pos.pojo.AskQQMessage;import com.market.pos.pojo.Backpack;import com.market.pos.tool.Equip.EquipService;import com.market.pos.tool.findTreasure.BackpackService;import javax.xml.crypto.Data;public class BackpackEvent {    public static String data_describe;    public static String data_type;    public static String data;    public static String data_value;    public static String ask;    public static void backpackEvent(String qqid,String groupid){        AskQQMessage askQQMessage = new AskQQMessage();        askQQMessage.setAct("101");        askQQMessage.setQQID(qqid);        askQQMessage.setGroupid(groupid);        Integer backpack_num = (int)(Math.random()*100);        System.out.println("随机数：" + backpack_num);        EquipService.backpackSelect(qqid);        Integer finddaily = EquipService.finddaily;        System.out.println("finddaily的值是------" + finddaily);        if (finddaily < 3) {            System.out.println("可以挖宝的用户");            finddaily = finddaily + 1;            String finddaily_str = finddaily.toString();            BackpackService.updateBackPack(qqid,"finddaily",finddaily_str);            if (backpack_num < 30) {                data_type = "nothing";                askQQMessage.setMsg("[CQ:at,qq=" + qqid + "] 你一锄头下去，啥也没挖到！");                ask = new Gson().toJson(askQQMessage);            }            if (backpack_num >= 30 && backpack_num <= 80) {                data_type = "money";                DataEvent.dataEvent(backpack_num);                data = DataEvent.data;                askQQMessage.setMsg("[CQ:at,qq=" + qqid + "] 获得金钱 " + data + " 金！");                ask = new Gson().toJson(askQQMessage);            }            if (backpack_num > 80 && backpack_num <= 90) {                data_type = "equipment";                DataEvent.dataEvent(backpack_num);                data = DataEvent.data;                data_value = DataEvent.data_value;                data_describe = DataEvent.data_describe;                askQQMessage.setMsg("[CQ:at,qq=" + qqid + "] 少侠福灵心至，本次挖宝中获得了 " + data + " !" +                        "\n[CQ:face,id=76]" + data_describe);                ask = new Gson().toJson(askQQMessage);            }            if (backpack_num > 90 && backpack_num <= 98) {                data_type = "book";                DataEvent.dataEvent(backpack_num);                data = DataEvent.data;                data_value = DataEvent.data_value;                data_describe = DataEvent.data_describe;                askQQMessage.setMsg("[CQ:at,qq=" + qqid + "] 少侠福灵心至，本次挖宝中获得了 " + data + " 秘籍!" +                        "\n[CQ:face,id=76]" + data_describe);                ask = new Gson().toJson(askQQMessage);            }            if (backpack_num > 98) {                data_type = "special";                DataEvent.dataEvent(backpack_num);                data = DataEvent.data;                data_value = DataEvent.data_value;                data_describe = DataEvent.data_describe;                askQQMessage.setMsg("[CQ:at,qq=" + qqid + "] 少侠福灵心至，本次挖宝中获得了 " + data + " !" +                        "\n[CQ:face,id=76]" + data_describe);                ask = new Gson().toJson(askQQMessage);            }        }        if (finddaily >= 3){            System.out.println("不能挖宝的用户");            askQQMessage.setMsg("[CQ:at,qq=" + qqid + "] 您今天已经挖宝三次了，请明日再来 !");            ask = new Gson().toJson(askQQMessage);            data_type = "没次数了";            data = "兜底";        }    }}