package com.market.pos.tool.connect;import com.market.pos.config.DBClose;import java.sql.*;public class JdbcUsers {    static String driver = "com.mysql.cj.jdbc.Driver";    static String user = "root";    static String password = "123456";    public static int grade;    public static int daily;    public static int pkdaily;    public static void searchGrade(String sql,String groupid){        String url = "jdbc:mysql://148.70.49.2:3306/" + groupid + "?useUnicode=true&characterEncoding=utf-8&serverTimezone=GMT%2B8";        grade = 0;        Connection connection = null;        Statement statement = null;        ResultSet result = null;        try{            Class.forName(driver);            connection = DriverManager.getConnection(url,user,password);            statement = connection.createStatement();            result = statement.executeQuery(sql);            while (result.next()){                grade = Integer.parseInt(result.getString("grade"));            }        }catch (ClassNotFoundException e) {            e.printStackTrace();        } catch (SQLException e) {            e.printStackTrace();        }finally {            DBClose.colseSqlConnection(result,statement,connection);        }    }    public static void searchDaily(String sql,String groupid){        String url = "jdbc:mysql://148.70.49.2:3306/" + groupid + "?useUnicode=true&characterEncoding=utf-8&serverTimezone=GMT%2B8";        Connection connection = null;        Statement statement = null;        ResultSet result = null;        try{            Class.forName(driver);            connection = DriverManager.getConnection(url,user,password);            statement = connection.createStatement();            result = statement.executeQuery(sql);            while (result.next()){                daily = Integer.parseInt(result.getString("daily"));            }        }catch (ClassNotFoundException e) {            e.printStackTrace();        } catch (SQLException e) {            e.printStackTrace();        }finally {            DBClose.colseSqlConnection(result,statement,connection);        }    }    public static void insert(String sql,String groupid){        String url = "jdbc:mysql://148.70.49.2:3306/" + groupid + "?useUnicode=true&characterEncoding=utf-8&serverTimezone=GMT%2B8";        Connection connection = null;        Statement statement = null;        ResultSet result = null;        try{            Class.forName(driver);            connection = DriverManager.getConnection(url,user,password);            statement = connection.createStatement();            statement.executeUpdate(sql);            if(result != null ){                System.out.println("插入成功！");            }else {                System.out.println("插入失败！");            }        }catch (ClassNotFoundException e) {            e.printStackTrace();        } catch (SQLException e) {            e.printStackTrace();        }finally {            DBClose.colseSqlConnection(result,statement,connection);        }    }    public static void update(String sql,String groupid){        String url = "jdbc:mysql://148.70.49.2:3306/" + groupid + "?useUnicode=true&characterEncoding=utf-8&serverTimezone=GMT%2B8";        Connection connection = null;        Statement statement = null;        ResultSet result = null;        try{            Class.forName(driver);            connection = DriverManager.getConnection(url,user,password);            statement = connection.createStatement();            statement.executeUpdate(sql);        }catch (ClassNotFoundException e) {            e.printStackTrace();        } catch (SQLException e) {            e.printStackTrace();        }finally {            DBClose.colseSqlConnection(result,statement,connection);        }    }    public static void searchPkdaily(String sql,String groupid){        String url = "jdbc:mysql://148.70.49.2:3306/" + groupid + "?useUnicode=true&characterEncoding=utf-8&serverTimezone=GMT%2B8";        Connection connection = null;        Statement statement = null;        ResultSet result = null;        try{            Class.forName(driver);            connection = DriverManager.getConnection(url,user,password);            statement = connection.createStatement();            result = statement.executeQuery(sql);            while (result.next()){                pkdaily = Integer.parseInt(result.getString("pkdaily"));                System.out.println("pkdaily : "+pkdaily);            }        }catch (ClassNotFoundException e) {            e.printStackTrace();        } catch (SQLException e) {            e.printStackTrace();        }finally {            DBClose.colseSqlConnection(result,statement,connection);        }    }}