package com.market.pos.tool.findTreasure;import com.market.pos.tool.connect.JdbcBackpack;public class BackpackService {    public static String equipment;    public static String money;    public static String book;    public static String special;    public static String resultUserId;    public static int finddaily;    /**     * 新增用户，只添加qqid     * @param qqid     */    public static void insertBackPack(String qqid){        String sql = "INSERT INTO backpack ( userid ) VALUES ("                + "'"+ qqid+ "'" +                ")";        JdbcBackpack.insertBackPack(sql);    }    /**     * 给qqid的用户更新相应的字段 equipment/meney/book/special/finddaily     * @param qqid     * @param data_type     * @param data     */    public static void updateBackPack(String qqid,String data_type,String data){        String sql = "update backpack set " + data_type + "=" + "'" +data+ "'" +"where userid = " + "'" +qqid+"'";        JdbcBackpack.updateBackPack(sql);    }    /**     * 通过qqid 获取 equipment/meney/book/special/finddaily     * @param qqid     */    public static void searchBackPack(String qqid){        String sql = "select * from backpack where userid = " +"'"+ qqid +"'";        JdbcBackpack.searchBackPack(sql);        resultUserId = JdbcBackpack.resultUserId;        money = JdbcBackpack.moneny;        equipment = JdbcBackpack.equipment;        special = JdbcBackpack.special;        book = JdbcBackpack.book;        finddaily = JdbcBackpack.finddaily;    }}