package com.market.pos.tool.findTreasure;import com.market.pos.tool.connect.Jdbc;import com.market.pos.tool.connect.JdbcFind;public class FindService {    public static String equipment;    public static String money;    public static String book;    public static String special;    public static int finddaily;    /**     * 新增用户，只添加qqid     * @param qqid     */    public static void insertBackPack(String qqid){        String sql = "INSERT INTO backpack ( userid ) VALUES ("                + "'"+ qqid+ "'" +                ")";        JdbcFind.insertBackPack(sql);    }    /**     * 给qqid的用户更新相应的字段 equipment/meney/book/special/finddaily     * @param qqid     * @param data_type     * @param data     */    public static void updateBackPack(String qqid,String data_type,String data){        String sql = "update backpack set " + data_type + "=" + "'" +data+ "'" +"where userid = " + "'" +qqid+"'";        JdbcFind.updateBackPack(sql);    }    /**     * 通过qqid 获取 equipment/meney/book/special/finddaily     * @param qqid     */    public static void searchBackPack(String qqid){        String sql = "select * from backpack where userid = " +"'"+ qqid +"'";        JdbcFind.searchBackPack(sql);        money = JdbcFind.moneny;        equipment = JdbcFind.equipment;        special = JdbcFind.special;        book = JdbcFind.book;        finddaily = JdbcFind.finddaily;    }}