package com.market.pos.tool.findTreasure;import com.google.gson.Gson;import com.market.pos.pojo.AskQQMessage;public class OpenBack {    public static String ask;    public static void openBack(String qqid,String groupid){        AskQQMessage askQQMessage = new AskQQMessage();        askQQMessage.setAct("101");        askQQMessage.setQQID(qqid);        askQQMessage.setGroupid(groupid);        BackpackService.searchBackPack(qqid);        String money = BackpackService.money;        String equipment = BackpackService.equipment;        String book = BackpackService.book;        String special = BackpackService.special;        if (money == null){            money = "0";        }        if (equipment == null){            equipment = "无";        }        if (book == null){            book = "无";        }        if (special == null){            special = "无";        }        askQQMessage.setMsg("[CQ:at,qq=" +qqid+ "] 你的背包中" +                "\n[CQ:face,id=158]总计金币：" + money +                "\n[CQ:face,id=69]装备：" + equipment +                "\n[CQ:emoji,id=128591]秘籍：" + book +                "\n[CQ:emoji,id=127881]特殊物品：" + special);        ask = new Gson().toJson(askQQMessage);    }}