package com.market.pos.tool.hideroom;public class IsUserIdNull {    public static void isUserIdNull(String userid,String qqid,String groupid){        if (userid == null){            hideRoomService.insertHideRoom(qqid,groupid);            hideRoomService.updateHideRoom(qqid,"do_hurt","1",groupid);            hideRoomService.updateHideRoom(qqid,"is_hurt","0",groupid);        }else {            hideRoomService.updateHideRoom(qqid, "do_hurt", "1",groupid);        }    }    public static void isUserIdNull_failed(String userid,String qqid,String groupid){        if (userid == null){            hideRoomService.insertHideRoom(qqid,groupid);            hideRoomService.updateHideRoom(qqid,"is_hurt","0",groupid);        }else {            hideRoomService.updateHideRoom(qqid, "is_hurt", "0",groupid);        }    }}