package com.market.pos.tool.hideroom;public class IsUserIdNull {    public static void isUserIdNull(String userid,String qqid){        if (userid == null){            hideRoomService.insertHideRoom(qqid);            hideRoomService.updateHideRoom(qqid,"do_hurt","1");            hideRoomService.updateHideRoom(qqid,"is_hurt","0");        }else {            hideRoomService.updateHideRoom(qqid, "do_hurt", "1");        }    }}