package com.market.pos.tool.lemoc;import org.java_websocket.client.WebSocketClient;import org.java_websocket.drafts.Draft_6455;import java.net.URI;public class LemocWebSocketClientFactory {    private static WebSocketClient webSocketClient = null;    public static WebSocketClient getLemocWebSocketClient() throws Exception{        if (webSocketClient == null){            webSocketClient = new LemocWebSocketClient(new URI("ws://127.0.0.1:25303") , new Draft_6455());        }        return webSocketClient;    }}