package com.market.pos.tool.pk;import com.market.pos.tool.connect.JdbcUsers;public class GetGrade {    public static int grade;    public static int update_grade;    public static int pkdaily;    public static int daily;    /**     * 通过qqid获取grade     * @param qqid     */    public static void getGrade(String qqid){        String sql = "select grade from users where userid = " + "'" + qqid + "'";        JdbcUsers.searchGrade(sql);        grade = JdbcUsers.grade;    }    /**     * 通过qqid和计算后的grade 去更新用户表     * @param qqid     * @param last_grade     */    public static void updateGrade(String qqid , int last_grade){        String sql = "update users set grade = " + "'" + last_grade + "'" + ","                + "pkdaily = '1' where userid = " + "'" + qqid + "'";        JdbcUsers.update(sql);        update_grade = JdbcUsers.grade;    }    /**     * 更新被攻击者数据     * @param qqid     * @param last_grade     */    public static void update_behurt_user(String qqid , int last_grade){        String sql = "update users set grade = " + "'" + last_grade + "'"                + " where userid = " + "'" + qqid + "'";        JdbcUsers.update(sql);        update_grade = JdbcUsers.grade;    }    /**     * 通过qqid获取当前用户的日挑战数 pkdaily     * @param qqid     */    public static void getPkdaily(String qqid){        String sql = "select pkdaily from users where userid = " + "'" + qqid + "'";        JdbcUsers.searchPkdaily(sql);        pkdaily = JdbcUsers.pkdaily;    }    /**     * 通过qqid获取当前用户的日签到数 daily     * @param qqid     */    public static void getDaily(String qqid){        String sql = "select daily from users where userid = " + "'" + qqid + "'";        JdbcUsers.searchDaily(sql);        daily = JdbcUsers.daily;    }    /**     * 向表 users 中 插入数据，默认 daily = 1 , pkdaily = 0     * @param qqid     * @param name     * @param new_grade     * @param update_time     */    public static void insertUsers(String qqid,String name,int new_grade,String update_time){        String sql = "INSERT INTO `users` (`userid`, `username`, `grade`, `update_time`, `daily`,`pkdaily`) VALUES ("                + "'" + qqid + "'" + ','  + "'" + name + "'" + ',' + "'" + new_grade + "'" + ','                + "'" + update_time + "'" + ',' + "'" + 1 + "'"  +',' + "'" + 0 + "'"  + ")";        JdbcUsers.insert(sql);    }    /**     * 通过qqid 更新 表users中的 grade /daily /update_time     * @param qqid     * @param last_grade     * @param time     */    public static void updateUsers(String qqid , int last_grade , String time){        String sql = "update users set grade = " + "'" + last_grade + "'" + ","                                    + "update_time = " + "'" + time + "'" + ","                                    + "daily = '1' where userid = " + "'" + qqid + "'";        JdbcUsers.update(sql);    }    public static void update_wuxiapk_daily(String qqid){        String sql = "update equip set finddaily = 1 where userid = " + "'" + qqid +"'";        JdbcUsers.update(sql);    }}