package com.market.pos.tool.pk;import java.util.regex.Matcher;import java.util.regex.Pattern;public class GetQid {    public static String ch_qqid;    public static String ch_msg;    public static String jineng;    public static String belongId;    /**     * 获取被艾特人的QQid     * @param msg     */    public static void getQid(String msg) {        String reg = "[^0-9]";        Pattern pattern = Pattern.compile(reg);        Matcher matcher = pattern.matcher(msg);        ch_qqid = matcher.replaceAll("").trim();    }    public static void getMsge(String msg){        String msg_start = "我要装备";        Pattern pattern = Pattern.compile(msg_start);        Matcher matcher = pattern.matcher(msg);        ch_msg = matcher.replaceAll("").trim();    }    public static void getMsgb(String msg){        String msg_start = "我要修炼";        Pattern pattern = Pattern.compile(msg_start);        Matcher matcher = pattern.matcher(msg);        ch_msg = matcher.replaceAll("").trim();    }    public static void getGroupId(String msg){        String msg_one = msg.replaceAll("我要在","");        String msg_two = msg_one.replaceAll("群闭关","");        if (msg_two.matches(".*皓水.*")){            belongId = "514869445";        }else if (msg_two.matches(".*帮会.*")){            belongId = "106102978";        }    }}