package com.market.pos.tool.pk;import java.util.regex.Matcher;import java.util.regex.Pattern;public class GetQid {    public static String ch_qqid;    public static String ch_msg;    public static String jineng;    public static String good;    public static String belongId;    /**     * 获取被艾特人的QQid     * @param msg     */    public static void getQid(String msg) {        String reg = "[^0-9]";        Pattern pattern = Pattern.compile(reg);        Matcher matcher = pattern.matcher(msg);        ch_qqid = matcher.replaceAll("").trim();    }    public static void getMsge(String msg){        String msg_start = "我要装备";        Pattern pattern = Pattern.compile(msg_start);        Matcher matcher = pattern.matcher(msg);        ch_msg = matcher.replaceAll("").trim();    }    public static void getCwName(String msg){        String msg_start = "我要锻造";        Pattern pattern = Pattern.compile(msg_start);        Matcher matcher = pattern.matcher(msg);        ch_msg = matcher.replaceAll("").trim();    }    public static void getMsgb(String msg){        String msg_start = "我要修炼";        Pattern pattern = Pattern.compile(msg_start);        Matcher matcher = pattern.matcher(msg);        ch_msg = matcher.replaceAll("").trim();    }    public static void getUserType(String msg){        String msg_start = "我要报名";        Pattern pattern = Pattern.compile(msg_start);        Matcher matcher = pattern.matcher(msg);        ch_msg = matcher.replaceAll("").trim();    }    public static void getGroupId(String msg){        String msg_one = msg.replaceAll("我要在","");        String msg_two = msg_one.replaceAll("群闭关","");        if (msg_two.matches(".*皓水.*")){            belongId = "514869445";        }else if (msg_two.matches(".*帮会.*")){            belongId = "106102978";        }else if (msg_two.matches(".*修真.*")){            belongId = "571175444";        }else if (msg_two.matches(".*大锤.*")){            belongId = "258512073";        }else if (msg_two.matches(".*狗带.*")){            belongId = "546665366";        }    }    public static void getSellGood(String msg){        String msg_start = "我要.*出";        Pattern pattern = Pattern.compile(msg_start);        Matcher matcher = pattern.matcher(msg);        good = matcher.replaceAll("").trim();    }}